import hashlib
import json

# We will use SHA256 as our hashing function
def sha256(data):
    hash_function = hashlib.sha256()
    hash_function.update(data.encode('utf-8'))
    return hash_function.hexdigest()

# Create a class for each block
class Block:
    def __init__(self, index, timestamp, data, previous_hash):
        self.index = index
        self.timestamp = timestamp
        self.data = data
        self.previous_hash = previous_hash
        self.hash = sha256(f'{self.index}{self.timestamp}{self.data}{self.previous_hash}')

# Create a class for the blockchain
class Blockchain:
    def __init__(self):
        self.chain = []

    def add_block(self, data):
        previous_hash = self.chain[-1].hash if len(self.chain) > 0 else None
        block = Block(len(self.chain), data['timestamp'], data['data'], previous_hash)
        self.chain.append(block)

    # This method will return the entire blockchain in JSON format
    def get_chain(self):
        return json.dumps([b.__dict__ for b in self.chain], indent=2)

class MinHeap:
    def __init__(self):
        self.heap= []

    def parent(self, i):
        return int((i - 1) / 2)

    def insertKey(self, k):
        self.heap.append(k)     
        i = len(self.heap)-1
        self.fix_up(i)

    def fix_up(self, i):
        while i != 0 and self.heap[self.parent(i)] > self.heap[i]:
            self.heap[i], self.heap[self.parent(i)] = self.heap[self.parent(i)], self.heap[i]
            i = self.parent(i)

    def extractMin(self):
        if len(self.heap) == 0:
            return -1
        min_elem = self.heap[0]
        if len(self.heap) > 1:
            self.heap[0] = self.heap.pop()
            self.fix_down(0)
        else:
            self.heap.pop()
        return min_elem

    def fix_down(self, i):
        left = 2*i + 1
        right = 2*i + 2
        min_i = i
        if left < len(self.heap) and self.heap[min_i] > self.heap[left]:
            min_i = left
        if right < len(self.heap) and self.heap[min_i] > self.heap[right]:
            min_i = right
        if min_i != i:
            self.heap[min_i], self.heap[i] = self.heap[i], self.heap[min_i]
            self.fix_down(min_i)

