class OrderedDict:
    def __init__(self):
        self.items = []

    def __setitem__(self, key, value):
        for item in self.items:
            if item[0] == key:
                item[1] = value
                break
        else:
            self.items.append([key, value])

    def __getitem__(self, key):
        for item in self.items:
            if item[0] == key:
                return item[1]
        raise KeyError(key)

    def __len__(self):
        return len(self.items)

