def knapsack(capacity, values, weights): 
    n = len(values) 

    # Initialize the two dimensional dp table with 0s 
    dp = [[0 for x in range(capacity + 1)] for x in range(n + 1)] 

    # Compute the values of the dp table 
    for i in range(n + 1): 
        for w in range(capacity + 1): 
            if i == 0 or w == 0: 
                dp[i][w] = 0
            elif weights[i - 1] <= w: 
                dp[i][w] = max(values[i - 1] + dp[i - 1][w - weights[i - 1]], dp[i - 1][w]) 
            else: 
                dp[i][w] = dp[i - 1][w] 

    # Get the optimal value from the last cell 
    return dp[n][capacity]

