def optimize_constraints(data):
    # Initialize optimization problem
    problem = Problem("Optimization problem")

    # Add variables to problem
    for key, value in data.items():
        for i in range(len(value)):
            var = problem.add_variable("{}_{}".format(key, i), type=float)
            # Add constraints
            problem.add_constraint(var == value[i], name="Constraint_{}_{}".format(key, i))

    # Solve problem
    problem.solve()

    # Return optimized constraints
    solution = {}
    for key, value in data.items():
        solution[key] = [problem.get_value("{}_{}".format(key, i))
                         for i in range(len(value))]

    return solution

class HeapSort: 

    def __init__(self, array): 
        self.array = array 

    def sort(self): 
        n = len(self.array) 

        # Build a maxheap 
        for i in range(n // 2 - 1, -1, -1): 
            self._heapify(n, i) 

        # One by one extract elements 
        for i in range(n - 1, 0, -1): 
            self.array[i], self.array[0] = self.array[0], self.array[i]  # swap 
            self._heapify(i, 0) 

    def _heapify(self, n, i): 
        largest = i  # Initialize largest as root 
        l = 2 * i + 1  # left = 2*i + 1 
        r = 2 * i + 2  # right = 2*i + 2 

        # See if left child of root exists and is 
        # greater than root 
        if l < n and self.array[i] < self.array[l]: 
            largest = l 

        # See if right child of root exists and is 
        # greater than root 
        if r < n and self.array[largest] < self.array[r]: 
            largest = r 

        # Change root, if needed 
        if largest != i: 
            self.array[i], self.array[largest] = self.array[largest], self.array[i]  # swap 

            # Heapify the root 
            self._heapify(n, largest)

