def detect_loop(head):
  fast_ptr = head
  slow_ptr = head

  while (slow_ptr and fast_ptr and fast_ptr.next):
      slow_ptr = slow_ptr.next
      fast_ptr = fast_ptr.next.next
      if slow_ptr == fast_ptr:
          return slow_ptr

  return None

def remove_loop(head, loop_ptr):
  slow_ptr = head
  fast_ptr = loop_ptr

  while (slow_ptr != fast_ptr):
      slow_ptr = slow_ptr.next
      fast_ptr = fast_ptr.next

  prev_slow_ptr = None
  while (slow_ptr != fast_ptr):
    prev_slow_ptr = slow_ptr
    slow_ptr = slow_ptr.next
    fast_ptr = fast_ptr.next

  prev_slow_ptr.next = None

class HashMap:

    def __init__(self, size): 
        self.size = size 
        self.map = [None] * size

    def _get_hash(self, key):
        hashCode = 0
        for char in key:
            hashCode += ord(char)
        return hashCode % self.size

    def add(self, key, value):
        keyHash = self._get_hash(key)

        keyValue = [key, value]
        if self.map[keyHash] is None:
            self.map[keyHash] = list([keyValue])
            return True
        else:
            for pair in self.map[keyHash]:
                if pair[0] == key:
                    pair[1] = value
                    return True
            self.map[keyHash].append(keyValue)
            return True

    def get(self, key):
        keyHash = self._get_hash(key)
        if self.map[keyHash] is None:
            return None
        else:
            for pair in self.map[keyHash]:
                if pair[0] == key:
                    return pair[1]
        return None

