"""

def get_prime_numbers(n):
    prime_numbers = []
    x = 2
    while len(prime_numbers) < n:
        is_prime = True
        for num in prime_numbers:
            if x % num == 0:
                is_prime = False
                break

        if is_prime:
            prime_numbers.append(x)

        x += 1

    return prime_numbers

