def tsp(graph): 

    # Set initial values: 
    best_path = [] 
    best_path_cost = float('Inf') 

    # Iterate through all the possible paths: 
    for i in range(1 << len(graph[0])): 

        # Generate the binary string: 
        path = [] 
        for j in range(len(graph[0])): 
            if (i & (1 << j)) > 0: 
                path.append(j) 

        # Calculate the cost of the path: 
        cost = 0
        k = 0
        for j in range(len(path)): 
            if j+1 < len(path): 
                cost += graph[path[j]][path[j+1]] 
            else: 
                cost += graph[path[j]][path[0]] 

        # Check if the cost of the path is less than the 
        # current best path cost: 
        if cost < best_path_cost: 
            best_path_cost = cost 
            best_path = path 

    return best_path, best_path_cost

class Node:
    def __init__(self, val):
        self.val = val
        self.left = None
        self.right = None

class BinarySearchTree:
    def __init__(self):
        self.root = None

    def add(self, val):
        new_node = Node(val)
        if not self.root:
            self.root = new_node
        else:
            curr = self.root
            while True:
                if val < curr.val:
                    if not curr.left:
                        curr.left = new_node
                        break
                    curr = curr.left
                else:
                    if not curr.right:
                        curr.right = new_node
                        break
                    curr = curr.right
        return self.root

    def search(self, val):
        curr = self.root
        while curr:
            if val < curr.val:
                curr = curr.left
            elif val > curr.val:
                curr = curr.right
            else:
                return curr
        return False

