def edit_distance(str1, str2): 

    # Initialize the DP matrix 
    m = len(str1) + 1
    n = len(str2) + 1
    dp_matrix = [[0 for x in range(n)] for x in range(m)] 

    for i in range(m): 
        for j in range(n): 
            if i == 0: 
                dp_matrix[i][j] = j    # Min. operations = j 
            elif j == 0: 
                dp_matrix[i][j] = i    # Min. operations = i 

            # If characters match 
            elif str1[i-1] == str2[j-1]: 
                dp_matrix[i][j] = dp_matrix[i-1][j-1] 

            # If characters don't match 
            else: 
                dp_matrix[i][j] = 1 + min(dp_matrix[i][j-1],        # Insert 
                                          dp_matrix[i-1][j],        # Remove 
                                          dp_matrix[i-1][j-1])    # Replace 

    return dp_matrix[m-1][n-1]

def generateHammingCode(bitString):
    # Get length of the input bit string
    n = len(bitString) 

    # Calculate the number of parity bits
    m = 0
    while 2**m < n + m + 1: 
        m+= 1

    # Initialize 2 empty lists to store parity and encoded bits
    parityBits = [] 
    encodedList = [] 

    # Calculate parity bits
    for i in range(m): 
        k = 2**i 
        parityBits.append(calculateParityBit(bitString, k)) 

    # Generate hamming code
    j = 0
    for i in range (1, n + m + 1): 
        if i == 2**j: 
            encodedList.append(parityBits[j])
            j += 1
        else :
            encodedList.append(int(bitString[i - j - 1]))

    return encodedList

def calculateParityBit(bitString, k): 
    counter = 0
    for i in range(len(bitString)): 
        if i & k == k :
            counter += int(bitString[i]) 
    if counter % 2 == 0: 
        return '0'
    else: 
        return '1'

