def binary_tree_to_linked_list(root): 
    if root is None:
        return None

    linked_list_head, linked_list_tail = None, None 

    queue = [root] 

    while queue: 
        node = queue.pop(0)

        #If linked_list_head element is None, insert node to it 
        if linked_list_head is None: 
            linked_list_head = node; 
            linked_list_tail = node 

        # Insert all the child nodes to the queue 
        if node.left: 
            queue.append(node.left) 
        if node.right: 
            queue.append(node.right) 

        # Link the node with the previous  
        # node created in linked list 
        if linked_list_tail: 
            linked_list_tail.next = node 
            linked_list_tail = node 

    return linked_list_head

