def is_palindrome_dna(dna):
    # Step 1: Extract the complementary strand by replacing each nucleotide with its complement in the strand 
    complement = ""
    for letter in dna:
        if letter == 'A':
            complement += 'T'
        elif letter == 'C':
            complement += 'G'
        elif letter == 'G':
            complement += 'C'
        elif letter == 'T':
            complement += 'A'
        else:
            print("Invalid nucleotide detected")

    # Step 2: Reverse the strand and compare it to its complementary strand
    reverse = complement[::-1]
    return reverse == dna

