def expression_parser(expression): 
    tokens = expression.split() 
    stack = [] 

    for token in tokens: 
        if token == '+' or token == '*' or token == '/': 
            a = stack.pop() 
            b = stack.pop() 
            if token == '+': 
                c = b + a 
            elif token == '*': 
                c = b * a 
            else: 
                c = b / a 
            stack.append(c) 
        else: 
            stack.append(int(token)) 
    return stack.pop()

