def calculateLevenshteinDistance(str1, str2): 
    n = len(str1)
    m = len(str2)

    # Create a matrix to store results 
    dp = [[0 for x in range(m + 1)] for x in range(n + 1)] 

    # Initialize the first row and column with the difference of lengths
    for i in range(n + 1): 
        for j in range(m + 1): 

            if i == 0: 
                dp[i][j] = j
            elif j == 0: 
                dp[i][j] = i

    # Iterate through the matrix and replace the 
    # cells with the required edit distance
    for i in range(1, n + 1): 
        for j in range(1, m + 1): 
            if str1[i-1] == str2[j-1]: 
                dp[i][j] = dp[i-1][j-1]
            else: 
                dp[i][j] = 1 + min(dp[i][j-1],        # Insert 
                                   dp[i-1][j],        # Remove 
                                   dp[i-1][j-1])    # Replace 

    return dp[n][m]

