def generate_parenthesis(n):
    result = []

    def generate_parens(left, right, n, curr_string):
        if left == n and right == n:
            result.append(curr_string)
            return

        # open a new parenthesis if one can still be opened
        if left < n:
            generate_parens(left + 1, right, n, curr_string + "(")

        # close a parenthesis if it's safe to do so
        if right < left:
            generate_parens(left, right + 1, n, curr_string + ")")

    generate_parens(0, 0, n, "")
    return result

