def knapsack(weights, values, max_weight):
    # Create a matrix to store results of subproblems
    mat = [[0 for i in range(max_weight + 1)] for i in range(len(weights) + 1)]

    # Fill mat[][] in bottom up manner
    for i in range(len(weights) + 1):
        for w in range(max_weight + 1):
            if i == 0 or w == 0:
                mat[i][w] = 0
            elif weights[i-1] <= w:
                mat[i][w] = max(values[i-1] + mat[i-1][w-weights[i-1]], mat[i-1][w])
            else:
                mat[i][w] = mat[i-1][w]

    # Find the maximum value
    max_value = mat[len(weights)][max_weight]

    return max_value

