class Node:
    def __init__(self, val):
        self.l = None
        self.r = None
        self.v = val

def tree_insert( node, val):
    if node is None:
        return Node(val)
    if val < node.v:
        node.l = tree_insert(node.l, val)
    else:
        node.r = tree_insert(node.r, val)

    return node

def show_tree(node):
    if node is None:
        return

    show_tree(node.l)

    print(f"{node.v}")

    show_tree(node.r)

