import random

def generate_account_number():
    # Generate a random Account number
    account_number = '00' 
    nums = [str(random.randint(0, 9)) for _ in range(7)]
    account_number += "".join(nums)
    checksum = get_checksum(account_number)
    account_number += str(checksum)
    return account_number

def get_checksum(account_number):
    n = 0
    for i, num in enumerate(account_number):
        n += int(num) * (i % 6 + 2)

    checksum = 11 - (n % 11)
    if checksum == 10:
        return 0
    elif checksum == 11:
        return 5
    else:
        return checksum

def sortList(arr):
    if len(arr) == 0:
        return []
    mid = len(arr) // 2
    left = arr[:mid]
    right = arr[mid:]

    left = sortList(left)
    right = sortList(right)
    return merge(left, right)

def merge(left, right):
    result = []
    leftIndex = 0
    rightIndex = 0

    while leftIndex < len(left) and rightIndex < len(right):
        if left[leftIndex] < right[rightIndex]:
            result.append(left[leftIndex])
            leftIndex += 1
        else:
            result.append(right[rightIndex])
            rightIndex += 1

    result += left[leftIndex:]
    result += right[rightIndex:]
    return result

class BinaryTree:
    def __init__(self, root):
        self.root = root


    def leastCommonAncestor(self, node1, node2):
        def recurseTree(node):
            if not node:
                return False

            left = recurseTree(node.left)
            right = recurseTree(node.right)

            mid = node == node1 or node == node2

            # If any two of the three flags left, right or mid become True.
            if mid + left +right >= 2:
                self.ancestor = node

            # Return True if either left or right or mid becomes True.
            return mid or left or right

        recurseTree(self.root)
        return self.ancestor

