class MaxPQ:
    def __init__(self, arr = []):
        self.heap = [0]
        for item in arr:
            self.add(item)

    def add(self, key):
        self.heap.append(key)
        self._swim(len(self.heap)-1)

    def delMax(self):
        root = self.heap[1]
        self.heap[1] = self.heap[-1]
        self.heap.pop()
        self._sink(1)
        return root

    def _swim(self, k):
        while k > 1 and self.heap[k] > self.heap[k//2]:
            temp = self.heap[k]
            self.heap[k] = self.heap[k//2]
            self.heap[k//2] = temp
            k = k//2

    def _sink(self, k):
        while 2*k <= len(self.heap)-1:
            j = 2*k
            if j < len(self.heap)-1 and self.heap[j] < self.heap[j+1]:
                j += 1
            if self.heap[j] > self.heap[k]:
                temp = self.heap[j]
                self.heap[j] = self.heap[k]
                self.heap[k] = temp
                k = j
            else:
