def levenshtein(s1, s2):
    l1 = len(s1)
    l2 = len(s2)

    matrix = [[0] * (l2 + 1) for _ in range(l1 + 1)]

    for i in range(l1 + 1):
        matrix[i][0] = i
    for j in range(l2 + 1):
        matrix[0][j] = j

    for i in range(1, l1 + 1):
        for j in range(1, l2 + 1):
            if s1[i - 1] == s2[j - 1]:
                cost = 0
            else:
                cost = 1
            matrix[i][j] = min(
                matrix[i - 1][j] + 1,
                matrix[i][j - 1] + 1,
                matrix[i - 1][j - 1] + cost,
            )

    return matrix[l1][l2]

